    let cardId:number = id;
    let handName:string = name;
  }
}

class Card {
  constructor (name:string) {
    cardId = newId(Card);
    let cardName:string = name;

    console.log(`Card ${cardId} with name "${cardName}" has been created.`);
  }
}

