class Deck {
  name:string;
  cards:Array<object>



  constructor(txt:string, n:string) {

    this.name = n;
    this.fetchCardList(this.createCardListFromTxt(txt));


  }


  async fetchCardList(list) {
    const cardList = list;
    const cardListKeys = Object.keys(cardList);
    const cardListNames = cardListKeys.map((key) => cardList[key].name);
    const cardListPromises:object = cardListNames.map(card => getCard(card));
    const cardListLoading = Promise.all(cardListPromises);
    const cardListCards = await cardListLoading;
    console.log(cardListCards);
    return cardListCards;

  }

  createCardListFromTxt(txt:string):Array<string> {
      let lines = txt.split("\n");
      let numLines = lines.length;
      let cardList = [];
      let idcount = 0;

      // parse
      for ( let i = 0 ; i < numLines ; i++ ) {
        let line = lines[i];

        if ( !((line.indexOf('/') == 0) || (line.indexOf(' ') == -1) || (line.indexOf('SB:') == 0)) )
        {
          let qty = line.match(/\d+/);
          let name = line.match(/\D+/);

          for ( let d = 0 ; d < qty[0] ; d++ ){
            cardList[ idcount++ ] = {
              'name': name[ 0 ].slice( 1 )
            }
          } //endfor
        } //endif
      }

      return cardList;
    }
}



